var should = require('should');
var NameGenerator = require('./../NameGenerator.js');

describe('NameGenerator', function(){

	it('Should return a random name', function(){
		var name = NameGenerator.getRandomName();
		name.should.be.type('string');
	});

	it('Should return a random name when pass "male" to getRandomNameByGender()', function(){
		var name = NameGenerator.getRandomNameByGender("male");
		name.should.be.type('string');
	});

	it('Should return a random name when pass "female" to getRandomNameByGender()', function(){
		var name = NameGenerator.getRandomNameByGender("female");
		name.should.be.type('string');
	});

	it('Should return two random names', function(){
		var randomNames = NameGenerator.getRandomNames(2);
		randomNames.length.should.be.equal(2);
	});

	it('Should return two random names by gender', function(){
		var randomNames = NameGenerator.getRandomNamesByGender(2, "male");
		randomNames.length.should.be.equal(2);
	});

	it('Should return ten random names', function(){
		var randomNames = NameGenerator.getRandomNames(10);
		randomNames.length.should.be.equal(10);
	});

	it('Should return ten random names', function(){
		var randomNames = NameGenerator.getRandomNamesByGender(10, "female");
		randomNames.length.should.be.equal(10);
	});

	it('Should empty array when pass 0 as parameter to getRandomNames()', function(){
		var randomNames = NameGenerator.getRandomNames(0);
		randomNames.length.should.be.equal(0);
	});

	it('Should empty array when pass -1 as parameter to getRandomNames()', function(){
		var randomNames = NameGenerator.getRandomNames(0);
		randomNames.length.should.be.equal(0);
	});

	it('Should return a fullname', function(){
		var fullName = NameGenerator.getFullName();
		fullName.should.be.type('string');
	});

	it('Should return a fullname by gender', function(){
		var fullName = NameGenerator.getFullNameByGender("male");
		fullName.should.be.type('string');
	});

	it('Should return a {name: "randomName", firstName: "randomName", lastName: "randomName"} when call to getFullNameObject()', function(){
		var fullName = NameGenerator.getFullNameObject();
		fullName.name.should.be.type('string');
		fullName.firstName.should.be.type('string');
		fullName.lastName.should.be.type('string');
	});

	it('Should return a {name: "randomName", firstName: "randomName", lastName: "randomName"} when call to getFullNameObjectByGender()', function(){
		var fullName = NameGenerator.getFullNameObjectByGender("male");
		fullName.name.should.be.type('string');
		fullName.firstName.should.be.type('string');
		fullName.lastName.should.be.type('string');
	});

});